package com.tech.exc;

public class Exc2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 30; i++) {
			if (i % 2 == 0) {
				System.out.println("cars with even registration");
			} else {
				System.out.println("cars with odd registration");
			}
		}
	}

}
