package com.tech.exc;
import java.util.*;
public class Excc19 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i =6;i>0;i--)
		{ int counter =1;
			for(int z =0;z<=counter;z++)
			System.out.print(" ");
			for(int j=1;j<=i;j++)
				System.out.print(j);
			for(int k =(i-1);k>0;k--)
				System.out.print(k);
			System.out.println();
			counter++;
		}
				
	}

}
